sys_ui_policy.script_false:
function onCondition() {
	g_form.setValue("adopted", false);
	g_form.clearValue("date_adopted");
	g_form.clearValue("owner");
}

sys_ui_policy.script_true:
function onCondition() {
	g_form.setValue("adopted", true);
	g_form.setValue("owner", g_user.userID);

// 	Calls the script includes in order to get the current date from current logged in user
// 	Then sets up the current value to the date_adopted field
	var date = new GlideAjax("GetCurrentDayTime");
	date.addParam("sysparm_name", "getDate");
	date.getXML(getDate);

	function getDate(response)
	{
		var answer = response.responseXML.documentElement.getAttribute("answer");
		g_form.setValue("date_adopted", answer);

	}






}
